INSERT INTO `qualification` (`qualification_id`,`type`,`description`,`level_num`,`title`) VALUES (1,'QUAL','[{\"level\":0,\"desc\":\"No minimum requirement.\"},{\"level\":1,\"desc\":\"Candidate should be in a client-serving role.\\n  Individuals working on internal assignments must be scheduled to rotate back\\n  to a client serving role in order to apply for certification.  No line management will be nominated.\\n  Candidate’s career objectives are focused on EM roles. The candidate\\n  explicitly expresses his will to belong to the EM Community.\"}]',5,'Focus on EM');
INSERT INTO `qualification` (`qualification_id`,`type`,`description`,`level_num`,`title`) VALUES (2,'QUAL','[{\"level\":0,\"desc\":\"No minimum requirement.\"},{\"level\":1,\"desc\":\"Candidate must have at least 1 year of experience\\n  working at Capgemini to qualify.  With\\n  less than 4 months at Capgemini, \\\"probationary\\\" certification is\\n  possible provided if Candidate can demonstrate experience requirements\\n  through previous external roles.  In\\n  this case, the EM The Capgemini Way course is considered mandatory.\"}]',5,'Capgemini History');
